mapper.select:
select c.brand_id,c.brand_name,c1.p_id,c1.brand_id as pbrand_id,c1.brand_name as pbrand_name from c_brand c left join c_brand c1 on c1.p_id=c.brand_id
c.status=0 and c.p_id is null
and c1.p_id=#{pId}

select tb.id, tb.type_id,t.type_name, tb.brand_id,b.brand_name, tb.create_time, tb.update_time, tb.status, tb.remark from c_type_brand tb
left join c_type t on tb.type_id=t.type_id
left join c_brand b on tb.brand_id=b.brand_id

    tb.status=0
     and tb.id = #{id}
     and tb.type_id = #{typeId}
     and tb.brand_id = #{brandId}
     and tb.create_time like CONCAT('%',DATE_FORMAT(#{createTime},'%Y-%m-%d'),'%')
     and tb.update_time like CONCAT('%',DATE_FORMAT(#{updateTime},'%Y-%m-%d'),'%')
     and tb.remark like CONCAT('%',#{remark},'%')
